node.code:


illuminate( $(from) ) 
{
  float atten = 1 / pow(length(L), $(Falloff));

  __inShadowC = $(Shadow);
  __category = $(category);
  __nondiffuse = 1 - $(Diffuse);
  __nonspecular = 1 - $(Specular);
  __ssslight = $(SSS);

  $(Cl) = atten * $(Kl) * $(LightColor);

  if ( $(IgnoreShadow) != 1 )
    $(Cl) =  (1 - __inShadowC) * $(Cl) +  __inShadowC * $(ShadowColor);
}
