	color	$(inShadow) = color( 0 );
	color	$(value) = color( 0 );
	uniform float $(count) = 0;

  illuminance( $(category), P, $(N), radians( $(angle) ), "lightcache", "refresh" )  
  {
    lightsource( "__inShadowC", $(inShadow) ); 	
    $(value) += $(inShadow);
    $(count) += 1;
  } 

  $(result) = clamp( $(value), color(0), color(1) ) * $(opacity);  /* value / count * opacity; */
  /* if ( count ) 
    result /= count; */
  if ( $(invert) )
    $(result) = 1 - $(result);
