node.code:


	vector $(V) = -normalize ( I );
	vector $(D) = reflect ( $(V), $(Nf) ) ;
  $(D) = vtransform ( "world", $(D) );

  #pragma nolint 4 /* Yes, we really DO want this noise in world space */
  float $(w) = 0.6 * noise( $(D) * 2.5 ) +
        0.2 * noise( $(D) * 5 ) +
        0.1 * noise( $(D) * 10 ) +
        0.05 * noise( $(D) * 20 );
    $(w) = $(w) * $(w) * $(w);

	$(result) = $(Kr) * $(w);
