node.code:
color
	$(inc_Ci) = 0,
	$(inc_Oi) = 0;
float $(inc_len) = 0;
uniform float
	$(blur) = $(ConeAngle) / 180,
	$(sampledAngle) = radians( $(ConeAngle) );
vector $(envDir) = vtransform( $(env_space), $(rflDir) );

gather ( "illuminance", P, $(rflDir), $(sampledAngle), $(samples),
	"maxdist", $(MaxDist),
	"surface:Ci", $(inc_Ci),
	"surface:Oi", $(inc_Oi),
	"ray:length", $(inc_len)
) {
	$(Ci) += $(inc_Ci);
	$(Oi) += $(inc_Oi);
	$(rayLength) += $(inc_len);
} else {
	$(Ci) += color environment( $(EnvMap_Ci), $(envDir),
		"blur", $(blur),
		"filter", $(filter),
		"width", $(filterWidth),
		"samples", $(env_samples),
		"expand", $(expand),
		"fill", $(fill_Ci)
	);
	$(Ci) += $(env_Ci);
	$(Oi) += environment( $(EnvMap_Oi), $(envDir),
		"blur", $(blur),
		"filter", $(filter),
		"width", $(filterWidth),
		"samples", $(env_samples),
		"expand", $(expand),
		"fill", $(fill_Oi)
	);
	$(Oi) += $(env_Oi);
	$(rayLength) += $(MaxDist);
}
$(Ci) /= $(samples);
$(Oi) /= $(samples);
$(rayLength) /= $(samples);
