float cosangle = normalize(-I).normalize(N);
if( cosangle < 0)
	$(result) = -pow(-cosangle, $(Attenuation));
else
	$(result) = pow(cosangle, $(Attenuation));
if( $(Abs) != 0 )
	$(result) = abs($(result));
if( $(Clamp) != 0 )
 	$(result) = clamp($(result), 0, 1);
if( $(Invert) != 0 )
	$(result) = 1 - $(result);
