node.code:
float $(x) = xcomp( $(Q) ); 
float $(y) = ycomp( $(Q) ); 

if ( $(direction) == "T" ) 
{
  $(x) = ycomp( $(Q) ); 
  $(y) = xcomp( $(Q) ); 
} 

float $(magnitude) = $(input);
float $(dampzone) = $(bspace) - $(tspace); 

if ( $(y) > $(tspace) ) 
{ 
  if ( $(y) < ( $(tspace) + $(dampzone) ) )  
    $(magnitude) *= ( $(y) - $(tspace) ) / $(dampzone) ; 
} 
else
  $(magnitude) = 0;

$(result) = $(magnitude); 

if( $(invert) ) 
  $(result) = 1 - $(result);
